mapper.insert:

		SELECT MP_BOARD_SEQ.NEXTVAL FROM DUAL

   INSERT INTO MP_BOARD(    BNO 
   					      , TITLE 
   					      , CONTENT 
   					      , WRITER  )
                 VALUES(    #{bno} 
                 		  , #{title}
                 		  , #{content}
                 		  , #{writer}  )

mapper.update:
UPDATE MP_BOARD
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
 WHERE BNO = #{bno}
